function PRO_Connect_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilelist_32': 'PRO_Connect_mobilelist_32',
        'mobilelistitem_33': 'PRO_Connect_mobilelistitem_33',
        'mobilelistitembutton_34': 'PRO_Connect_mobilelistitembutton_34'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.mappings["PRO_Connect_restservice2_onsuccess_mapping_0"] = {
        "homeScreen": "PRO_Connect",
        "directions": [

        {
            "from_name": "restservice2",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "PRO_Connect",
            "to_type": "UI",

            "mappings": [

            {

                "source": "$['body'][i]",
                "target": "$['mobilelistitem_33']"

            },

            {

                "source": "$['body'][i]['CategoryName']",
                "target": "$['mobilelistitem_33:text']"

            }

            ]
        }

        ]
    };

    Apperyio.mappings["PRO_Connect_restservice2_onbeforesend_mapping_0"] = {
        "homeScreen": "PRO_Connect",
        "directions": [

        {

            "to_name": "restservice2",
            "to_type": "SERVICE_REQUEST",

            "to_default": {
                "headers": {
                    "X-Appery-Database-Id": "{database_id}"
                },
                "parameters": {},
                "body": null
            },

            "mappings": []
        }

        ]
    };

    Apperyio.datasources = Apperyio.datasources || {};

    window.restservice2 = Apperyio.datasources.restservice2 = new Apperyio.DataSource(Topics_Categories_list_service, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["PRO_Connect_restservice2_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {

        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["PRO_Connect_restservice2_onsuccess_mapping_0"]);
        },
        "onError": function(jqXHR, textStatus, errorThrown) {}
    });

    Apperyio.CurrentScreen = 'PRO_Connect';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var PRO_Connect_onLoad = function() {
            PRO_Connect_elementsExtraJS();

            try {
                restservice2.execute({});
            } catch (e) {
                console.error(e);
                hideSpinner();
            };

            PRO_Connect_deviceEvents();
            PRO_Connect_windowEvents();
            PRO_Connect_elementsEvents();
        };

    // screen window events


    function PRO_Connect_windowEvents() {

        $('#PRO_Connect').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function PRO_Connect_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function PRO_Connect_elementsExtraJS() {
        // screen (PRO_Connect) extra code

        /* mobilelist_32 */

        listView = $("#PRO_Connect_mobilelist_32");
        theme = listView.attr("data-theme");
        if (typeof theme !== 'undefined') {
            var themeClass = "ui-btn-up-" + theme;
            listItem = $("#PRO_Connect_mobilelist_32 .ui-li-static");
            $.each(listItem, function(index, value) {
                $(this).addClass(themeClass);
            });
        }

        /* mobilelistitem_33 */

    };

    // screen elements handler


    function PRO_Connect_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#PRO_Connect_mobilecontainer [name="mobilelistitem_33"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('DescriptionPage', {
                        transition: 'slide',
                        reverse: false
                    });
                    try {
                        $a.storage["lon"].update("$", $a.c15r($a.UIHandler.resolveGeneratedComponent('mobilelistitem_33', this), 'get', 'text'))
                    } catch (e) {
                        console.error(e)
                    };

                }
            },
        }, '#PRO_Connect_mobilecontainer [name="mobilelistitem_33"]');

    };

    $(document).off("pagebeforeshow", "#PRO_Connect").on("pagebeforeshow", "#PRO_Connect", function(event, ui) {
        Apperyio.CurrentScreen = "PRO_Connect";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    PRO_Connect_onLoad();
};

