function maps_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'googlemap_2': 'maps_googlemap_2',
        'marker_3': 'maps_marker_3'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'maps';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var maps_onLoad = function() {
            maps_elementsExtraJS();

            maps_deviceEvents();
            maps_windowEvents();
            maps_elementsEvents();
        };

    // screen window events


    function maps_windowEvents() {

        $('#maps').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function maps_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function maps_elementsExtraJS() {
        // screen (maps) extra code

        /* googlemap_2 */

        $("[name = 'googlemap_2']").wrap("<div/>");
        $("[name = 'googlemap_2']").parent().css("margin-left", $("[name = 'googlemap_2']").css("margin-left"));
        $("[name = 'googlemap_2']").parent().css("margin-right", $("[name = 'googlemap_2']").css("margin-right"));
        $("[name = 'googlemap_2']").css("margin-left", '0');
        $("[name = 'googlemap_2']").css("margin-right", '0');

        var googlemap_2_options = {
            markerSourceName: "googlemap_2_markers",
            latitude: "",
            longitude: "",
            address: "San Francisco, CA",
            zoom: 10,
            showLocationMarker: false
        }

        Apperyio.__registerComponent('googlemap_2', new Apperyio.ApperyMapComponent("googlemap_2", googlemap_2_options));
        $("[name='googlemap_2_markers'] [apperytype='marker']").attr("reRender", "googlemap_2");
        $(":mobile-pagecontainer").off("pagecontainershow.maps_mobilecontainer").on("pagecontainershow.maps_mobilecontainer", function(event, ui) {
            if (($('#maps_googlemap_2', ui.toPage).length > 0) && (Apperyio('googlemap_2') != undefined)) {
                Apperyio('googlemap_2').refresh();
            }
        });

    };

    // screen elements handler


    function maps_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

    };

    $(document).off("pagebeforeshow", "#maps").on("pagebeforeshow", "#maps", function(event, ui) {
        Apperyio.CurrentScreen = "maps";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    maps_onLoad();
};

$(document).off("pagecreate", "#maps").on("pagecreate", "#maps", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    maps_js();
});